const dayjs = require('dayjs')

function FormatStringData(data) {
  let day  = data.split("/")[0];
  let month  = data.split("/")[1];
  let year  = data.split("/")[2];

  return year + '/' + ("0"+month).slice(-2) + '/' + ("0"+day).slice(-2);
}

let dateOfBirthBR = '19/08/2005'
let dateOfBirth = FormatStringData(dateOfBirthBR)

const yearOfBirth = dayjs(dateOfBirth).format('YYYY')
const birthMonth = dayjs(dateOfBirth).format('MM')
const birthDay = dayjs(dateOfBirth).format('DD')

const currentYear = dayjs().format("YYYY")
const currentMonth = dayjs().format('MM')
const currentDay = dayjs().format('DD')

const nextBirthdayEUA = `${currentYear}/${birthMonth}/${birthDay}`
const nextBirthdayBR = `${birthDay}/${birthMonth}/${currentYear}`

const daysToTheNextBirthday = dayjs(nextBirthdayEUA).diff(`${currentYear}/${currentMonth}/${currentDay}`, "days")

console.log(`
Idade:
Próximo aniversário: ${nextBirthdayBR}
Dias até completar  anos: ${daysToTheNextBirthday} dias`)

